void loop() { //Main function
  
  ValueSaver(); //Run the ValueSaver function
  Serialprint1(); //Run the Serialprint1 function (deleted after coding is completed)
  
  if(digitalRead(success)){ //If an input signal comes in success
    WordDistinguish(); //Run the WordDistinguish function
  }
  else if(digitalRead(cancel)){ //If an input signal comes in cancel
   SavedAlphabetDelete(); //Run the SavedAlphabetDelete function
  }
  else if (value[0] != 0 || value[1] != 0 || value[2] != 0 || value[3] != 0 || value[4] != 0 || value[5] != 0) { //If a value is entered
    delay(1000); //1.5 second delay
    ValueSaver(); //Run the ValueSaver function
    Distinguish(); //Run the Distinguish function
    Serialprint2(); //Run the Serialprint2 function(deleted after coding is completed)
    OverlapProtection(); //Run the OverlapProtection function
  }
  else{ ; } 
}

