void ValueSaver() { //Save the value of the pressure sensor in the value variable
  int i = 0;
  for (i = 0; i < 6; i++) {
    value[i] = digitalRead(fsr[i]);
    delay(10);
  }
}

void Distinguish() { //Distinguish the alphabet represented by the read value
  int j = 0, i = 0;
  for (i = 0; i < 26; i++) {
    for (j = 0; j < 6; j++) {
      if (value[j] != alphabet[i][j]) {
        j = 0;
        break;
      }
      else { ; }
    }
    if (j == 6) {
      AlphabetSaver(i); //Run the AlphabetSaver function including i value
      break;
    }
  }
}

