void WordDistinguish(){ //Distinguish words
  if(wordnumber){ wordnumber --; }
  int j = 0, i = 0;
  for (i = 0; i < 26; i++) {
    for (j = wordnumber; j >= 0; j--) {
      if (words[i][j+1] >= '97' && words[i][j+1] <= '122'){
        break;
      }
      if (wordcombination[j] != words[i][j]) {
        break;
      }
      else { ; }
    }
    if (j == -1) {
      WordPrint(i);
      break;
    }
  }
  wordnumber = 0;
  OverlapProtection(); //Run the OverlapProtection function
  Reset(); //Run the Reset function
  delay(200);
}

